import logo from "../logo.svg";
import Menus from "../Components/Menus";
import Menu2 from "../Components/Menu2";
import Footer from "../Components/Footer";
import StarIcon from "@mui/icons-material/Star";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import DoneIcon from "@mui/icons-material/Done";
import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate, useParams } from "react-router-dom";

function EventsChallengePg() {
  const UserID = "6437e8a2f1f4e07db756ee8f";

  const navigate = useNavigate();
  let { id } = useParams();

  const [eventsUpcom, setEvents] = useState({ Participants: [] });
  const [parts, setPart] = useState([]);
  const [isParticipating, setIsParticipating] = useState(false);

  useEffect(() => {
    function getEvents() {
      axios
        .get(`http://localhost:8070/Events/getEvent/${id}`)
        .then((res) => {
          setEvents(res.data);
          console.log(res.data);
          console.log(isParticipating);
        })
        .catch((err) => {
          alert(err.message);
        });
    }
    
    getEvents();
  }, []);

  useEffect(() => {
    setIsParticipating(eventsUpcom?.Participants.includes(UserID));
  }, [eventsUpcom]);

  function changeDate(date) {
    var mydate = new Date(date);
    var month = [
      "January",
      "February",
      "March",
      "April",
      "May",
      "June",
      "July",
      "August",
      "September",
      "October",
      "November",
      "December",
    ][mydate.getMonth()];
    var cont;
    switch (mydate.getDate() % 10) {
      case 1:
        cont = "st";
        break;
      case 2:
        cont = "nd";
        break;
      case 1:
        cont = "rd";
        break;
      default:
        cont = "th";
    }
    var str =
      mydate.getDate() + cont + " " + month + " " + mydate.getFullYear();
    return str;
  }

  function changePart(part) {
    for (let i = 0; i < 3; i++) {
      console.log(part[i]);
    }
  }

  function sendData(e) {
    e.preventDefault();

    const newParticipant = {
      id,
      UserID,
    };
    axios
      .put(`http://localhost:8070/Events/addParticipants`, newParticipant)
      .then(() => {
        alert("Marked as Going");
        document.location.reload();
      })
      .catch((err) => {
        alert(err);
      });
  }

  return (
    <div className="App">
      <Menus />
      <Menu2 />
      <div className="moreInfo-events">
        <div className="moreInfo-image">
          <img id="moreInfo-pic" src={eventsUpcom.EventPhoto} />
        </div>
        <div className="moreInfo-details">
          <div className="content" id="type">
            Event
          </div>
          <div className="content" id="title">
            {eventsUpcom.EventType}
          </div>
          <div className="content" id="participants">
            {eventsUpcom.NumberParticipants} Going{" "}
          </div>
          <div className="content" id="dateTitle">
            Event Date
          </div>
          <div className="content" id="date">
            {changeDate(eventsUpcom.DateEvent)}
          </div>
          <div className="content" id="description">
            {eventsUpcom.Description}
          </div>
          <div className="content" id="list">
            <ul id="list">
              <li>
                Expected Participants :{" "}
                <span id="mi-info">{eventsUpcom.ExpectedParticipants} </span>
              </li>
              <li>
                Required Items :{" "}
                <span id="mi-info">{eventsUpcom.RequiredItems} </span>
              </li>
              <li>
                More Details : <span id="mi-info">{eventsUpcom.Notes} </span>
              </li>
            </ul>
          </div>
          <Button
            variant="primary"
            id="participate"
            type="submit"
            onClick={sendData}
          >
            <DoneIcon id="add" />
            <div className="btn-title">Mark as Participating</div>
          </Button>
        </div>
      </div>

      <div className="moreInfo-reviews">
        <div className="moreInfo-custReview">
          <div className="reviews">
            <div className="content" id="review-title">
              Reviews
            </div>

            <div className="moreInfo-reviewCust">
              <div className="moreInfo-reviewCust-profile">
                <div className="moreInfo-reviewCust-profilePic">
                  <img
                    id="review-profile-pic"
                    src={require("../Assets/background-food-symbols-6213980.jpg")}
                  />
                </div>
